import React, { Component } from 'react';
import Newsitem from './Newsitem';
import axios from 'axios';

export default class News extends Component {
  state = {
    articles: [],
    loading: true, // Set loading to true initially
    error: null, // To handle any error that occurs
    currentPage: 1,
    articlesPerPage: 6,
  };

  async componentDidMount() {
    await this.fetchArticles();
  }

  fetchArticles = async () => {
    try {
      const response = await axios.get('/api/news?q=Nellore'); // Use the proxy endpoint
      this.setState({ articles: response.data.articles, loading: false });
    } catch (error) {
      console.error('Error fetching news:', error);
      this.setState({ loading: false, error: 'Failed to load articles. Please try again later.' });
    }
  };

  handlePageChange = (pageNumber) => {
    this.setState({ currentPage: pageNumber });
  };

  render() {
    const { articles, loading, error, currentPage, articlesPerPage } = this.state;

    if (loading) {
      return <h2>Loading...</h2>;
    }

    if (error) {
      return <h2>{error}</h2>; // Display error message if any
    }

    const indexOfLastArticle = currentPage * articlesPerPage;
    const indexOfFirstArticle = indexOfLastArticle - articlesPerPage;
    const currentArticles = articles.slice(indexOfFirstArticle, indexOfLastArticle);

    const pageNumbers = [];
    for (let i = 1; i <= Math.ceil(articles.length / articlesPerPage); i++) {
      pageNumbers.push(i);
    }

    return (
      <div className="container my-3">
        <h2 className="text-center">NUSE EXCLUSIVE HEADLINES OF NELLORE</h2>
        <div className="row">
          {currentArticles.map((article, index) => (
            <div className="col-md-4" key={index}>
              <Newsitem 
                title={article.title} 
                description={article.description} 
                imageUrl={article.urlToImage} 
                newsUrl={article.url} 
              />
            </div>
          ))}
        </div>
        <div className="d-flex justify-content-center my-3">
          <nav>
            <ul className="pagination">
              {pageNumbers.map(number => (
                <li key={number} className={`page-item ${currentPage === number ? 'active' : ''}`}>
                  <button onClick={() => this.handlePageChange(number)} className="page-link">
                    {number}
                  </button>
                </li>
              ))}
            </ul>
          </nav>
        </div>
      </div>
    );
  }
}
